
public class CirculateType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
          for(int i=0;i<10;i++)
          {
        	 System.out.println(i);
        	  
          }
          System.out.println("===========================================================");
          
          int m=0;
          
          do {
        	  m++;
        	  System.out.println(m);
        	  
          }
          while(m<10);
          
          System.out.println("===========================================================");
          
          int n=10;
          
          while(n>0)
          {
        	  System.out.println(n);
        	  n--;
          }
          
		
		
	}

}
